import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Parcel } from '../interfaces/parcel.interface';
import { environment } from '../../environments/environment';

@Component({
  selector: 'app-fetch-parcel-data',
  templateUrl: './fetch-parcel-data.component.html',
  styleUrls: ['./fetch-parcel-data.component.css']
})
export class FetchParcelDataComponent {
  public parcels: Parcel[] = [];
  private filePath = 'assets/input.txt';

  constructor(private http: HttpClient) { }

  ngOnInit(): void {
    this.fetchParcels();
  }

  private fetchParcels(): void {
    const apiUrl = environment.baseUrl + 'parcel';
    this.http.get<Parcel[]>(apiUrl).subscribe(
      (parcels: Parcel[]) => {
        this.parcels = parcels;
      },
      (error: any) => {
        console.error(error);
      }
    );
  }
}
<div *ngFor="let parcel of parcels">
    <h3 class="text-white">Sample Parcel Weight: {{ parcel.sampleParcelWeight }}</h3>
    <ul>
        <li *ngFor="let item of parcel.items">
            <span>Item ID: {{ item.index }}</span>
            <span>Item Name: {{ item.name }}</span>
            <span>Item Cost: {{ item.weight }}</span>
            <span>Item Weight: {{ item.cost }}</span>
        </li>
    </ul>
</div>
