import { Component, Input, OnInit, AfterViewInit } from '@angular/core';
import { FormArray, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Item } from '../interfaces/item.interface';

@Component({
  selector: 'app-packing-form',
  templateUrl: './packing-form.component.html',
  styleUrls: ['./packing-form.component.css']
})
export class PackingFormComponent implements OnInit, AfterViewInit {
  @Input() parcelData: any[] = [];
  @Input() availableItems: Item[] = []; // Items fetched from the API
  @Input() sampleParcelWeights: number[] = []; // Sample parcel weights fetched from the API

  packingForm: FormGroup = new FormGroup({});
  //selectedItems: { weight: number, items: Item[] }[] = [];
  selectedItem: Item | null = null; // Update this line to store the selected item
  itemsByWeight: Record<number, any[]> = {};
  filteredItems: any[] = []; // New property for filtered items

  constructor(private fb: FormBuilder) { }

  ngOnInit(): void {
    this.initForm();
    this.updateItemsByWeight();
    console.log('Parcel Data:', this.parcelData);
    console.log('Available Items:', this.availableItems);
    console.log('Sample Parcel Weights:', this.sampleParcelWeights);
  }



  initForm(): void {
    this.packingForm = this.fb.group({
      weightLimit: ['', Validators.required],
      items: this.fb.array([])
    });

    this.packingForm.get('weightLimit')?.valueChanges.subscribe((selectedWeight) => {
      this.filterItemsByWeight(selectedWeight);
    });
  }

  get items(): FormArray {
    return this.packingForm.get('items') as FormArray;
  }

  createItemGroup(item: Item): FormGroup {
    return this.fb.group({
      index: [item.index, Validators.required],
      weight: [`${item.weight}kg`],
      cost: [`€${item.cost}`]
    });
  }

  addItem(): void {
    const emptyItem: Item = { index: '', name: '', weight: 0, cost: '' };
    this.items.push(this.createItemGroup(emptyItem));
    // Call onWeightSelected after the item is added
    setTimeout(() => {
      this.onWeightSelected();
    });
  }

  removeItem(index: number): void {
    this.items.removeAt(index);
  }

  filterItemsByWeight(selectedWeight: number): void {
    this.filteredItems = this.itemsByWeight[selectedWeight] || [];
  }

  updateItemsByWeight(): void {
    for (const weight of this.sampleParcelWeights) {
      const items = this.availableItems.filter(item => item.weight === weight);

      this.itemsByWeight[weight] = items.map((item) => ({
        value: item.index,
        label: `${item.index}, ${item.name}`,
        weight: `${item.weight}kg`,
        cost: `€${item.cost}`
      }));
    }
  }

  submitForm(): void {
    if (this.packingForm.valid) {
      const formValue = this.packingForm.value;
      console.log(formValue);
    }
  }

  ngAfterViewInit(): void {
    this.onWeightSelected();
  }

  onWeightSelected(): void {
    const weightLimit = this.packingForm.get('weightLimit')?.value;
    const itemsArray = this.packingForm.get('items') as FormArray;
    const lastItemIndex = itemsArray.length - 1;
    const selectedItemIndex = itemsArray.at(lastItemIndex)?.get('index')?.value;

    console.log('Weight Limit:', weightLimit);
    console.log('Last Item Index:', lastItemIndex);
    console.log('Selected Item Index:', selectedItemIndex);

    if (weightLimit !== null && selectedItemIndex !== undefined) {
      const selectedItems = this.itemsByWeight[weightLimit];
      console.log('Selected Items:', selectedItems);

      const selectedItem = selectedItems?.find(item => item.value === selectedItemIndex);
      console.log('Selected Item:', selectedItem);

      this.selectedItem = selectedItem ? this.availableItems.find(item => item.index === selectedItemIndex) || null : null;
    } else {
      this.selectedItem = null;
    }
  }



}
<div class="container-fluid">
    <div class="row">
        <div class="col-12">
            <div class="d-flex justify-content-center mt-5">
                <img src="../../assets/logo.png" alt="App Logo" class="app-logo">
            </div>
        </div>
    </div>

    <div class="row">
        <div class="col-12">
            <form class="p-5 packingForm" [formGroup]="packingForm" (ngSubmit)="submitForm()">
                <h2 class="text-light mb-4">Choose a Sample Shipping Box</h2>

                <div class="form-group">
                    <label for="weight" class="text-light my-2">Weight</label>
                    <!-- Weight dropdown -->
                    <select class="form-control" id="weight" formControlName="weightLimit" (change)="onWeightSelected()">
                        <option value="">-- Select Weight Limit --</option>
                        <option *ngFor="let weightLimit of sampleParcelWeights" [value]="weightLimit">{{ weightLimit }}kg</option>
                    </select>
                </div>

                <div formArrayName="items">
                    <div class="item-group" *ngFor="let itemGroup of items.controls; let i = index" [formGroupName]="i">
                        <div class="form-row w-50 mt-4">
                            <div class="col">
                                <div class="form-group">
                                    <label class="text-light" for="selectedItem">Select Item:</label>
                                    <select class="form-control" formControlName="index" (change)="onWeightSelected()">
                                        <option [ngValue]="null">-- Select Item --</option>
                                        <option *ngFor="let option of itemsByWeight[packingForm.get('weightLimit')?.value]" [ngValue]="option.value">
                                            {{ option.label }}
                                        </option>
                                    </select>
                                </div>
                            </div>
                            <div class="col">
                                <label class="text-light">Weight</label>
                                <input type="text" class="form-control" formControlName="weight" [value]="selectedItem?.weight || ''" readonly>
                            </div>
                            <div class="col">
                                <label class="text-light">Cost</label>
                                <div class="input-group">
                                    <input type="text" class="form-control" formControlName="cost" [value]="selectedItem?.cost || ''" readonly>
                                </div>
                            </div>
                            <div class="d-flex justify-content-start">
                                <button type="button" class="btn btn-yellow my-2" (click)="removeItem(i)">Remove Item</button>
                            </div>
                        </div>
                
                    </div>
                </div>
                <button type="button" class="btn btn-yellow py-2 my-2" (click)="addItem()">Add Item</button>
                <button type="submit" [disabled]="!packingForm.valid || items.length === 0" class="btn btn-yellow mx-2 py-2">Submit</button>
            </form>
        </div>
    </div>
</div>
