import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, FormArray, Validators } from '@angular/forms';

@Component({
  selector: 'app-packing-form',
  templateUrl: './packing-form.component.html',
  styleUrls: ['./packing-form.component.css']
})
export class PackingFormComponent implements OnInit {
  packingForm: FormGroup = new FormGroup({});

  constructor(private fb: FormBuilder) { }

  ngOnInit(): void {
    this.initForm();
  }

  initForm(): void {
    this.packingForm = this.fb.group({
      weightLimit: ['', Validators.required],
      items: this.fb.array([])
    });
  }

  get items(): FormArray {
    return this.packingForm.get('items') as FormArray;
  }

  addItem(): void {
    const itemGroup = this.fb.group({
      weight: ['', Validators.required],
      cost: ['', Validators.required]
    });

    this.items.push(itemGroup);
  }

  removeItem(index: number): void {
    this.items.removeAt(index);
  }

  submitForm(): void {
    if (this.packingForm.valid) {
      // TODO: Implement the logic to send the form data to the server
      const formValue = this.packingForm.value;
      console.log(formValue);
    }
  }
}
<div class="container-fluid">
    <div class="row">
        <div class="col-12">
            <div class="d-flex justify-content-center mt-5">
                <img src="../../assets/logo.png" alt="App Logo" class="app-logo">
            </div>
        </div>
    </div>

    <div class="row">
        <div class="col-12">
            <form class="p-5 packingForm" [formGroup]="packingForm" (ngSubmit)="submitForm()">
                <h2 class="text-light mb-4">Enter Parcel Details</h2>

                <div class="form-group">
                    <label for="weight" class="text-light my-2">Weight</label>
                    <input type="number" class="form-control" id="weight" placeholder="Enter weight" formControlName="weightLimit">
                </div>

                <div class="form-group">
                    <label for="items" class="text-light">Items:</label>
                    <div formArrayName="items">
                        <div *ngFor="let item of items.controls; let i = index" [formGroupName]="i" class="item-group">
                            <input type="number" formControlName="weight" class="form-control mr-2" placeholder="Weight">
                            <input type="number" formControlName="cost" class="form-control mx-2" placeholder="Cost">
                            <button type="button" (click)="removeItem(i)" class="btn btn-yellow">Remove</button>
                        </div>
                    </div>
                    <button type="button" (click)="addItem()" class="btn btn-yellow py-2 my-2">Add Item</button>
                </div>

                <button type="submit" [disabled]="!packingForm.valid" class="btn btn-yellow">Submit</button>
            </form>
        </div>
    </div>
</div>
