import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormArray, FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-packing-form',
  templateUrl: './packing-form.component.html',
  styleUrls: ['./packing-form.component.css']
})
export class PackingFormComponent implements OnInit {
  packingForm: FormGroup = new FormGroup({});
  sampleWeights: number[] = [];
  allItems: any[] = [];
  availableItems: any[] = [];

  constructor(private fb: FormBuilder, private http: HttpClient) { }

  ngOnInit(): void {
    this.initForm();
    this.loadSampleWeights();
    this.loadAvailableItems();
    const filePath = 'assets/input.txt';
    this.http.get(filePath, { responseType: 'text' }).subscribe(
      (response) => {
        this.allItems = this.extractItems(response); // Load all items
      },
      (error) => {
        console.error(error);
      }
    );
  }

  initForm(): void {
    this.packingForm = this.fb.group({
      weightLimit: ['', [Validators.required, Validators.max(100)]],
      items: this.fb.array([])
    });

    this.packingForm.get('weightLimit')?.valueChanges.subscribe(() => {
      this.filterItemsByWeight();
    });
  }

  get items(): FormArray {
    return this.packingForm.get('items') as FormArray;
  }

  addItem(item?: any): void {
    const weightLimit = this.packingForm.get('weightLimit')?.value;
    if (weightLimit) {
      const itemGroup = this.fb.group({
        index: [item ? item.index : '', Validators.required],
        weight: [item ? item.weight : '', Validators.required],
        cost: [item ? item.cost : '', Validators.required]
      });
      this.items.push(itemGroup);
    }
  }

  removeItem(index: number): void {
    this.items.removeAt(index);
  }

  submitForm(): void {
    if (this.packingForm.valid) {
      const formValue = this.packingForm.value;
      console.log(formValue);
    }
  }

  loadSampleWeights(): void {
    const filePath = 'assets/input.txt';
    this.http.get(filePath, { responseType: 'text' }).subscribe(
      (response) => {
        const weights = this.extractWeights(response);
        this.sampleWeights = weights; // Assign the sample weights from the API response
        this.filterItemsByWeight(); // Filter items based on the selected weight
      },
      (error) => {
        console.error(error);
      }
    );
  }

  extractWeights(data: string): number[] {
    const lines = data.split('\n');
    const weights: number[] = [];
    for (const line of lines) {
      const parts = line.split(':');
      if (parts.length === 2) {
        const weight = parseFloat(parts[0].trim());
        if (!isNaN(weight)) {
          weights.push(weight);
        }
      }
    }
    return weights;
  }

  loadAvailableItems(): void {
    // Load the available items from API or any other source
    // For demo purposes, let's use a static list
    this.availableItems = [
      { index: 0, name: 'Item 1', weight: 1.5, cost: 10 },
      { index: 1, name: 'Item 2', weight: 2.0, cost: 15 },
      { index: 2, name: 'Item 3', weight: 0.5, cost: 5 }
    ];
  }

  extractItems(data: string): any[] {
    const lines = data.split('\n');
    const items: any[] = [];

    for (const line of lines) {
      const colonIndex = line.indexOf(':');
      if (colonIndex !== -1) {
        const weight = parseFloat(line.substring(0, colonIndex).trim());
        const itemData = line.substring(colonIndex + 1).split('(');

        for (const item of itemData) {
          const itemInfo = item.replace(/[\)\s]/g, '').split(',');
          if (itemInfo.length === 3) {
            const id = parseInt(itemInfo[0]);
            const itemWeight = parseFloat(itemInfo[1]);
            const cost = parseFloat(itemInfo[2].substring(1));

            items.push({
              id,
              weight: itemWeight,
              cost
            });
          }
        }
      }
    }

    return items;
  }

  onItemSelection(event: any): void {
    const selectedIndex = parseInt(event.target.value);
    const selectedWeight = parseFloat(this.packingForm.get('weightLimit')?.value);
    const selectedItems = this.availableItems.filter(item => item.weight <= selectedWeight);
    const selectedItem = selectedItems.find(item => item.index === selectedIndex);
    if (selectedItem) {
      this.packingForm.get('weight')?.setValue(selectedItem.weight);
      this.packingForm.get('cost')?.setValue(selectedItem.cost);
    }
  }


  filterItemsByWeight(): void {
    const selectedWeight = parseFloat(this.packingForm.get('weightLimit')?.value);

    // Clear existing items
    while (this.items.length !== 0) {
      this.items.removeAt(0);
    }

    // Filter items based on selected weight
    this.availableItems = this.allItems.filter(item => item.weight <= selectedWeight);

    // Log available items for each sample parcel
    this.sampleWeights.forEach((weight, index) => {
      const itemsForParcel = this.loadAllItems(weight);
      console.log(`Available items for sample parcel ${index + 1}:`, itemsForParcel);
    });
  }

  loadAllItems(weight: number): any[] {
    const itemsForParcel: any[] = [];
    let remainingWeight = weight;

    // Iterate over available items
    for (const item of this.availableItems) {
      if (item.weight <= remainingWeight) {
        itemsForParcel.push(item);
        remainingWeight -= item.weight;
      }
    }
    console.log(`Items for weight ${weight}:`, itemsForParcel); // Log the items for the current weight
    return itemsForParcel;
  }
}
<div class="container-fluid">
    <div class="row">
        <div class="col-12">
            <div class="d-flex justify-content-center mt-5">
                <img src="../../assets/logo.png" alt="App Logo" class="app-logo">
            </div>
        </div>
    </div>

    <div class="row">
        <div class="col-12">
            <form class="p-5 packingForm" [formGroup]="packingForm" (ngSubmit)="submitForm()">
                <h2 class="text-light mb-4">Enter Parcel Details</h2>

                <div class="form-group">
                    <label for="weight" class="text-light my-2">Weight</label>
                    <select class="form-control" id="weight" formControlName="weightLimit" (change)="filterItemsByWeight()">
                        <option *ngFor="let weightLimit of sampleWeights" [value]="weightLimit">{{ weightLimit }}</option>
                    </select>
                </div>

                <div class="form-group">
                    <label for="items" class="text-light mt-2">Items:</label>
                    <div formArrayName="items">
                        <div *ngFor="let item of items.controls; let i = index" [formGroupName]="i" class="item-group">
                            <div class="form-row">
                                <div class="col">
                                    <div class="form-group">
                                        <label for="selectedItem">Select Item:</label>
                                        <select class="form-control" id="index" formControlName="index" (change)="onItemSelection($event)">

                                            <option value="">-- Select Item --</option>
                                            <option *ngFor="let item of availableItems" [value]="item.index">{{ item.name }}</option>
                                        </select>
                                    </div>
                                </div>
                                <div class="col">
                                    <label class="text-light">Weight</label>
                                    <input type="number" formControlName="weight" class="form-control" placeholder="Weight">
                                </div>
                                <div class="col">
                                    <label class="text-light">Cost</label>
                                    <div class="input-group">
                                        <div class="input-group-prepend">
                                            <span class="input-group-text">€</span>
                                        </div>
                                        <input type="number" formControlName="cost" class="form-control" placeholder="Cost">
                                    </div>
                                </div>
                            </div>
                            <button type="button" (click)="removeItem(i)" class="btn btn-yellow">Remove</button>
                        </div>
                    </div>
                    <button type="button" (click)="addItem()" [disabled]="!packingForm.get('weightLimit')?.value" class="btn btn-yellow py-2 my-2">Add Item</button>
                </div>
                <button type="submit" [disabled]="!packingForm.valid || !packingForm.get('weightLimit')?.value" class="btn btn-yellow">Submit</button>
            </form>
        </div>
    </div>
</div>
