import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { catchError, map } from 'rxjs/operators';
import { Observable, of } from 'rxjs'
import { Item } from '../interfaces/item.interface';
import { Parcel } from '../interfaces/parcel.interface';

@Injectable({
  providedIn: 'root'
})
export class ParcelDataService {
  private fileUrl = 'https://gist.githubusercontent.com/Lynne88git/552c061f4ebe867b70f6bc2376653b02/raw/876ca05861ce3bed421e0970b24563dfdfeb7ca5/input.txt';

  constructor(private http: HttpClient) { }

  fetchParcels(): Observable<Parcel[]> {
    return this.http.get(this.fileUrl, { responseType: 'text' }).pipe(
      map((data: string) => this.parseParcels(data)),
      catchError((error: any) => {
        console.error(error);
        return of([]);
      })
    );
  }

  private parseParcels(data: string): Parcel[] {
    const parcelLines = data.split('\n');
    const parcels: Parcel[] = [];

    for (const parcelLine of parcelLines) {
      const parcelData = parcelLine.trim().split(' : ');
      const parcelId = Number(parcelData[0]);
      const itemData = parcelData[1].split(') ');

      const items: Item[] = [];

      for (const itemString of itemData) {
        const itemInfo = itemString.substring(itemString.indexOf('(') + 1).split(', ');
        const itemId = Number(itemInfo[0]);
        const itemName = itemInfo[1];
        const itemWeight = parseFloat(itemInfo[2]);
        const itemPrice = parseFloat(itemInfo[3].replace('€', ''));

        const item: Item = {
          index: itemId.toString(),
          name: itemName,
          weight: itemWeight,
          cost: itemPrice
        };

        items.push(item);
      }

      const sampleParcelWeight = items.reduce((totalWeight, item) => totalWeight + item.weight, 0);

      const parcel: Parcel = {
        id: parcelId,
        sampleParcelWeight: sampleParcelWeight,
        items: items
      };

      parcels.push(parcel);
    }

    return parcels;
  }

  //fetchItems(): Promise<Item[]> {
